import React from "react";
import styles from "./Table.module.css";
import { useNavigate } from "react-router-dom";
import { getTeamClass } from "./Table";

export default function SingleTable({ date, location, homeTeam, awayTeam }) {
    const navigate = useNavigate();

    // 테이블 행 클릭 시 실행되는 함수

    return (
        <div className={styles.tableWrapper}>
            <table className={styles.table}>
                <tbody>
                <tr className={styles.tr}>
                    <td className={styles.td}>{date}</td>
                    <td className={styles.td}>{location}</td>
                    <td className={styles.td}>
                            <span className={`${styles.highlight} ${getTeamClass(homeTeam)}`}>
                                {homeTeam}
                            </span>
                    </td>
                    <td>vs</td>
                    <td className={styles.td}>
                            <span className={`${styles.highlight} ${getTeamClass(awayTeam)}`}>
                                {awayTeam}
                            </span>
                    </td>
                </tr>
                </tbody>
            </table>
        </div>
    );
}
