import styles from "./Players.module.css";
import React, { useEffect, useState } from "react";
import Footer from "../../components/_Layout/Footer";
import HeaderImg from "../../image/playerHeader.jpg";
import PlayerTable from "../../components/_Table/_PTable";

export default function Players() {
    const [players, setPlayers] = useState([]);
    const [view, setView] = useState("pitcher");

    useEffect(() => {
        fetch(`/api/player`)
            .then(res => res.json())
            .then((data) => {
                setPlayers(data); // 전체 플레이어 상태 업데이트
                console.log(data);
            })
            .catch((err) => {
                console.error("API fetch error:", err);
            });
    }, []);

    const filterPlayers = (position) => {
        if (position === "pitcher") {
            return players.filter(player => player.playerPosition === 1); // 투수만 필터
        } else {
            return players.filter(player => player.playerPosition !== 1); // 타자만 필터
        }
    };
    function toggleToobarStatus() {
        const checkbox = document.getElementById('toolbar-active'); // 체크박스를 가져옴
        const isChecked = checkbox.checked; // 체크박스가 체크되어 있는지 여부 확인

        if (isChecked) {
            // 체크박스가 체크되었을 때의 동작
            console.log("Toolbar is active");
        } else {
            // 체크박스가 체크되지 않았을 때의 동작
            console.log("Toolbar is inactive");
        }

    }
    return (
        <div className={styles.default}>
            <div className={styles.cropping}>
                <img className={styles.headerImg} src={HeaderImg} alt="Header" />
            </div>
            <div className={styles.inner}>
                <div className={styles.title}>선수 목록</div>

                <div className={styles.checkboxSwitch}>
                    <input
                        type="checkbox"
                        checked=""
                        onChange="toggleToobarStatus()"
                        value="1"
                        name="status"
                        className={styles.inputCheckbox}
                        id={styles.toolbarActive}
                    />
                    <div className={styles.checkboxAnimate}>
                        <span className={styles.checkboxOff}>OFF</span>
                        <span className={styles.checkboxOn}>ON</span>
                    </div>
                </div>


                <div className={styles.buttonContainer}>
                    <button
                        className={styles.toggleButton}
                        onClick={() => setView("pitcher")}
                    >
                        투수 보기
                    </button>
                    <button
                        className={styles.toggleButton}
                        onClick={() => setView("hitter")}
                    >
                        타자 보기
                    </button>
                </div>
                <br/>

                {/* 헤더는 pPosition에 따라 동적으로 렌더링되므로 */}
                <PlayerTable pId={0} pPosition={view === "pitcher" ? 1 : 0}/>
                {filterPlayers(view).map(data => {
                    if (view === "hitter") {
                        return (
                            <PlayerTable
                                key={data.playerId}
                                pId={data.playerId}
                                pName={data.playerName}
                                pPosition={data.playerPosition}
                                pTeam={data.teamName}
                                pSponsor={data.teamSponsor}
                                avg={data.avg}
                                ops={data.ops}
                                war={data.war}
                            />
                        );
                    } else {
                        return (
                            <PlayerTable
                                key={data.playerId}
                                pId={data.playerId}
                                pName={data.playerName}
                                pPosition={data.playerPosition}
                                pTeam={data.teamName}
                                pSponsor={data.teamSponsor}
                                era={data.era}
                                ip={data.ip}
                                whip={data.whip}
                            />
                        );
                    }
                })}

            </div>
            <Footer/>
        </div>
    );
}
