import { useNavigate } from "react-router-dom";
import React, {useState} from "react";
import styles from "./Signin.module.css"
import axios from "axios"

export default function Signup() {
    const navigate = useNavigate();

    const [username, setUsername] = useState('');
    const [password, setUserpw] = useState('');
    // 버튼 상태
    
    const saveId = event =>{
        setUsername(event.target.value);
        console.log(event.target.value);
    }

    const savePw = event =>{
        setUserpw(event.target.value);
        console.log(event.target.value);
    }


    const handleSignUp = () => {
        const data = {
            username,
            password,
        };

        axios
            .post("http://localhost:8080/member/signUp", data)
            .then((res) => {
                console.log(res);
            })
            .catch((err) => {
                console.log(`${err} :: 회원가입 실패`);
            });
    };
    
    

    return (
        <div className={styles.signupPage}>
            <div>
            <div className={styles.title}>크보로그</div>
                <div className={styles.inputArea}>
                <input className={styles.inputId} value={username} onChange={saveId} type="text"
                    placeholder="ID를 입력해주세요." />
                <button  className="text-wrapper-7, div-wrapper">중복확인</button>
                </div>
                <div className={styles.inputArea}>

                <input value={password} onChange={savePw} type="password"
                    placeholder="비밀번호를 입력해주세요." />
                    </div>
                <button onClick={handleSignUp} className={styles.signin}>회원가입</button>

            </div>
        </div>
    );

};
