import styles from "./Mypage.module.css";
import React, {useEffect, useState} from "react";
import Footer from "../../components/_Layout/Footer";
import SingleTable from "../../components/_Table/SingleTable";

import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import { faPenToSquare} from "@fortawesome/free-regular-svg-icons";
import {useNavigate} from "react-router-dom";

export default function Mypage() {
    const [user, setUser] = useState([]);
    const [rootTeam, setRootTeam] = useState([]);
    const navigate = useNavigate();
    const handleLogEdit = () => {
        navigate(`/my/edit`);
    };
    useEffect(() => {
        fetch("/api/my")
            .then(res => res.json())
            .then(res => {
                setUser(res);
                setRootTeam(res.rootTeam.teamName)
            })
            .catch((err) => {
                console.error("API fetch error:", err); // ÏóêÎü¨ Ï≤òÎ¶¨
            });
    }, []);



    return (
        <div className={styles.default}>
            <div className={styles.cropping}>
            </div>
            <div className={styles.inner}>
                <div>
                    <FontAwesomeIcon className={styles.ii} icon={faPenToSquare} onClick={handleLogEdit}/>
                    <p className={styles.emoji}>üíü</p>
                    <p className={styles.title}>ÎßàÏù¥ÌéòÏù¥ÏßÄ</p>
                    <SingleTable
                        iconName={"faUser"}
                        type={"ÏïÑÏù¥Îîî"}
                        data={user.username}
                    />
                    <SingleTable
                    iconName={"faBaseball"}
                    type={"ÏùëÏõê ÌåÄ"}
                    data={rootTeam}
                    />
                    <SingleTable
                        iconName={"faCalendarCheck"}
                        type={"ÏùëÏõê ÎÇ†Ïßú"}
                        data={user.rootdate}
                    />
                    <p className={styles.content}></p>
                </div>
            </div>
            <Footer/>
        </div>
    );
}
