import styles from "./Teampage.module.css";
import React, {useEffect, useState} from "react";
import Footer from "../../components/_Layout/Footer";
import HeaderImg from "../../image/nail.png";
import {useNavigate} from "react-router-dom";

export default function Teampage() {
    const [teamImage, setTeamImage] = useState("");
    const [team, setTeam] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        fetch("/api/my")
            .then(res => res.json())
            .then(res => {
                setTeam(res.rootTeam)
            })
            .catch((err) => {
                console.error("API fetch error:", err); // 에러 처리
            });

        let imagePath = "";
        switch (team.sponsor) {
            case "기아":
                imagePath = require("../../image/kia.png");
                break;
            case "삼성":
                imagePath = require("../../image/samsung.png");
                break;
            case "LG":
                imagePath = require("../../image/LG.jpg");
                break;
            default:
                imagePath = require("../../image/nail.png");
                break;
        }
        setTeamImage(imagePath);
    }, [team.sponsor]);


    return (
        <div className={styles.default}>
            <div className={styles.cropping}>
                <img className={styles.headerImg} src={teamImage} alt="Header" />
            </div>
            <div className={styles.inner}>
                <div className={styles.title}>{team.teamName}</div>
                <br/>s


            </div>
            <Footer />
        </div>
    );
}
