import styles from "./Mypage.module.css";
import React, {useEffect, useState} from "react";
import Footer from "../../components/_Layout/Footer";
import SingleTable from "../../components/_Tool/SingleTable";

import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faMarker} from "@fortawesome/free-solid-svg-icons";

export default function Mypage() {
    const [teams, setTeams] = useState([]);
    const [teamName, setTeamName] = useState("");

    // ÏùëÏõê ÌåÄ ÏÑ†ÌÉù
    useEffect(() => {
        fetch("/api/my/team") // ÌåÄ Î™©Î°ùÏùÑ Í∞ÄÏ†∏Ïò§Îäî API ÏóîÎìúÌè¨Ïù∏Ìä∏
            .then((res) => res.json())
            .then((data) => {
                setTeams(data); // Í∞ÄÏ†∏Ïò® Îç∞Ïù¥ÌÑ∞Î•º ÏÉÅÌÉúÏóê Ï†ÄÏû•
            })
            .catch((err) => {
                console.error("Failed to fetch teams:", err);
            });
    }, []);

    // ÌåÄ ÏÑ†ÌÉù Ìï∏Îì§Îü¨
    const handleTeamSelect = (e) => {
        const teamId = e.target.value;
        setTeamName(teamName);
    };

    // Ìèº Ï†úÏ∂ú Ìï∏Îì§Îü¨
    const handleSubmit = (e) => {
        e.preventDefault();

        // ÏÑ†ÌÉùÌïú ÌåÄ Îç∞Ïù¥ÌÑ∞ ÏÑúÎ≤ÑÎ°ú Ï†ÑÏÜ°
        fetch(`/api/my/selectTeam`, {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({
                teamName: teamName,
            }),
        })
            .then((res) => res.json().then((data) => ({ ok: res.ok, message: data })))
            .then(({ ok, message }) => {
                if (ok) {
                    alert("ÌåÄ ÏÑ†ÌÉù ÏôÑÎ£å!");
                } else {
                    alert(`Ïò§Î•ò: ${message}`);
                }
            })
            .catch((err) => {
                console.error("ÌåÄ ÏÑ†ÌÉù Ïã§Ìå®:", err);
                alert("ÏÑúÎ≤ÑÏôÄÏùò ÌÜµÏã† Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
            });
    };


    return (
        <div className={styles.default}>
            <div className={styles.cropping}>
            </div>
            <div className={styles.inner}>
                <div>
                    <FontAwesomeIcon className={styles.ii} icon={faMarker}/>
                    <p className={styles.emoji}>üíü</p>
                    <p className={styles.title}>ÎßàÏù¥ÌéòÏù¥ÏßÄ</p>
                    <form onSubmit={handleSubmit}>
                        <div className={styles.formGroup}>
                            {teams.length > 0 ? (
                                <select
                                    name="teamName"
                                    value={teamName}
                                    onChange={handleTeamSelect}
                                    required
                                    className={styles.input}
                                >
                                    <option value="" disabled>
                                        ÏùëÏõêÌï† ÌåÄÏùÑ ÏÑ†ÌÉùÌïòÏÑ∏Ïöî
                                    </option>
                                    {teams.map((team) => (
                                        <option key={team.teamName} value={team.teamName}>
                                            {team.teamName}
                                        </option>
                                    ))}
                                </select>
                            ) : (
                                <p>ÌåÄ Î™©Î°ùÏùÑ Î∂àÎü¨Ïò§Îäî Ï§ëÏûÖÎãàÎã§...</p>
                            )}
                        </div>
                        <button type="submit" className={styles.submitButton}>
                            ÌåÄ ÏÑ†ÌÉù
                        </button>
                    </form>

                    <p className={styles.content}></p>
                </div>
            </div>
            <Footer/>
        </div>
    );
}
