import styles from "./Mypage.module.css";
import React, {useEffect, useState} from "react";
import Footer from "../../components/_Layout/Footer";
import SingleTable from "../../components/_Tool/SingleTable";

import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faMarker} from "@fortawesome/free-solid-svg-icons";

export default function Mypage() {
    const [teams, setTeams] = useState([]);
    const [selectedTeamId, setSelectedTeamId] = useState("");

    // 응원 팀 선택
    useEffect(() => {
        fetch("/api/my/team") // 팀 목록을 가져오는 API 엔드포인트
            .then((res) => res.json())
            .then((data) => {
                setTeams(data); // 가져온 데이터를 상태에 저장
            })
            .catch((err) => {
                console.error("Failed to fetch teams:", err);
            });
    }, []);

    // 팀 선택 핸들러
    const handleTeamSelect = (e) => {
        const teamId = e.target.value;
        setSelectedTeamId(teamId); // 선택한 팀 ID 저장
        console.log("Selected team ID:", teamId);
    };

    // 폼 제출 핸들러
    const handleSubmit = (e) => {
        e.preventDefault();

        // 선택한 팀 데이터 서버로 전송
        fetch(`/api/my/selectTeam`, {
            method: "PUT",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({
                teamId: selectedTeamId,
            }),
        })
            .then((res) => {
                if (res.ok) {
                    alert("팀 선택 완료!");
                } else {
                    throw new Error("Failed to save selected team");
                }
            })
            .catch((err) => {
                console.error("Failed to save selected team:", err);
            });
    };


    return (
        <div className={styles.default}>
            <div className={styles.cropping}>
            </div>
            <div className={styles.inner}>
                <div>
                    <FontAwesomeIcon className={styles.ii} icon={faMarker}/>
                    <p className={styles.emoji}>💟</p>
                    <p className={styles.title}>마이페이지</p>
                    <form onSubmit={handleSubmit}>
                        <div className={styles.formGroup}>
                            {teams.length > 0 ? (
                                <select
                                    name="teamId"
                                    value={selectedTeamId}
                                    onChange={handleTeamSelect}
                                    required
                                    className={styles.input}
                                >
                                    <option value="" disabled>
                                        응원할 팀을 선택하세요
                                    </option>
                                    {teams.map((team) => (
                                        <option key={team.teamId} value={team.teamId}>
                                            {team.teamName}
                                        </option>
                                    ))}
                                </select>
                            ) : (
                                <p>팀 목록을 불러오는 중입니다...</p>
                            )}
                        </div>
                        <button type="submit" className={styles.submitButton}>
                            팀 선택
                        </button>
                    </form>

                    <p className={styles.content}></p>
                </div>
            </div>
            <Footer/>
        </div>
    );
}
