            .then(res=>res.json())
            .then((data) => {
                setPlayer(data);
                setPData({
                    ...data,
                    scoreData:
                        data.playerPosition === 1
                            ? {
                                bb: data.bb,
                                era: data.era,
                            }
                            : {
                                avg: data.avg,
                                obp: data.obp,
