import React, {useState} from "react";
import styles from "./Signup.module.css"
import axios from "axios"

export default function Signup() {

    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [checkDup, setCheckDup] = useState(false);

    const handleSignUp = () => {
        const data = {
            username,
            password,
        };

        axios
            .post("/api/signup", data)
            .then((res) => {
                console.log("회원가입 성공", data, res);
            })
            .catch((err) => {
                console.log(`${err} :: 회원가입 실패`);
            });


    };

    const checkDupUser = () => {
        axios
        .post("/api/checkDupUser", username)
            .then((res) => {
                setCheckDup(true);
                console.log("중복 체크 성공", username, res);
            })
            .catch((err) => {
                console.log(`${err} :: 중복임`);
            });
    }

    return (
        <div className={styles.signupPage}>
            <div className={styles.signupContainer}>
                <div className={styles.title}>크보로그</div>
                <div className={styles.inputGroup}>
                    <input
                        className={styles.inputField}
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        type="text"
                        placeholder="ID"
                    />
                    <button onClick={checkDupUser} className={styles.duplicateCheckButton}>중복확인</button>
                </div>
                <div className={styles.inputGroup}>
                    <input
                        className={styles.inputField}
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        type="password"
                        placeholder="비밀번호"
                    />
                </div>
                <button onClick={handleSignUp} className={styles.signupButton}>
                    회원가입
                </button>
            </div>
        </div>
    );


};
