import styles from "./Players.module.css";
import React, { useEffect, useState } from "react";
import Footer from "../../components/_Layout/Footer";
import HeaderImg from "../../image/playerHeader.jpg";
import PlayerTable from "../../components/_Table/_PTable";

export default function Players() {
    const [players, setPlayers] = useState([]);
    const [view, setView] = useState("all");  // "all", "pitcher", "hitter" 중 하나

    useEffect(() => {
        fetch(`/api/player`)
            .then(res => res.json())
            .then((data) => {
                setPlayers(data); // 전체 플레이어 상태 업데이트
            })
            .catch((err) => {
                console.error("API fetch error:", err);
            });
    }, []);

    // 데이터 필터링 함수
    const filterPlayers = (position) => {
        if (position === "pitcher") {
            return players.filter(player => player.playerPosition === 1); // 투수만 필터
        } else if (position === "hitter") {
            return players.filter(player => player.playerPosition !== 1); // 타자만 필터
        }
        return players; // 모두 표시
    };

    return (
        <div className={styles.default}>
            <div className={styles.cropping}>
                <img className={styles.headerImg} src={HeaderImg} alt="Header" />
            </div>
            <div className={styles.inner}>
                <div className={styles.title}>선수 목록</div>

                {/* 버튼을 통해 보기 방식 선택 */}
                <div className={styles.buttonContainer}>
                    <button
                        className={styles.toggleButton}
                        onClick={() => setView("pitcher")}
                    >
                        투수 보기
                    </button>
                    <button
                        className={styles.toggleButton}
                        onClick={() => setView("hitter")}
                    >
                        타자 보기
                    </button>
                    <button
                        className={styles.toggleButton}
                        onClick={() => setView("all")}
                    >
                        전체 보기
                    </button>
                </div>
                <br />

                {/* 필터링된 선수 리스트 */}
                {filterPlayers(view).map(data => (
                    <PlayerTable
                        key={data.playerId}
                        pId={data.playerId}
                        pName={data.playerName}
                        pPosition={data.playerPosition}
                        pTeam={data.teamName}
                        pSponsor={data.teamSponsor}
                        avg={data.avg}
                        ops={data.ops}
                        war={data.war}
                        era={data.era}
                        ip={data.ip}
                        whip={data.whip}
                    />
                ))}
            </div>
            <Footer />
        </div>
    );
}
