import styles from "./Teampage.module.css";
import React, {useEffect, useState} from "react";
import Footer from "../../components/_Layout/Footer";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";

export default function Teampage() {
    const [team, setTeam] = useState([]);
    const [backgroundColor, setBackgroundColor] = useState("#f0f0f0");

    const [gameData, setGameData] = useState([]);
    const [selectedDate, setSelectedDate] = useState(new Date()); // 달력에서 선택된 날짜
    const [gamesForSelectedDate, setGamesForSelectedDate] = useState([]); // 선택된 날짜의 경기 정보

    const formatDate = (date) => {
        const d = new Date(date);
        const year = d.getFullYear();
        const month = String(d.getMonth() + 1).padStart(2, '0');
        const day = String(d.getDate()).padStart(2, '0');
        const hours = String(d.getHours()).padStart(2, '0');
        const minutes = String(d.getMinutes()).padStart(2, '0');
        return `${year}-${month}-${day} ${hours}:${minutes}`;
    };

    const formattedDeadline = formatDate(updatedResult.deadline);
    const formattedResultDate = formatDate(updatedResult.result_date);

    useEffect(() => {
        fetch("/api/my")
            .then(res => res.json())
            .then(res => {
                setTeam(res.rootTeam)
            })
            .catch((err) => {
                console.error("API fetch error:", err); // 에러 처리
            });
        console.log(team)
        switch (team.sponsor) {
            case "기아":
                setBackgroundColor("#F73600"); // 기아의 배경색
                break;
            case "두산":
                setBackgroundColor("#131230"); // 두산의 배경색
                break;
            case "삼성":
                setBackgroundColor("#074CA1"); // 삼성의 배경색
                break;
            default:
                setBackgroundColor("#f0f0f0"); // 기본 배경색
        }
    }, [team.sponsor]);

    useEffect(() => {
        const selectedDate = new Date();
        const year = selectedDate.getFullYear();
        const month = selectedDate.getMonth(); // 월은 0부터 시작하므로 (예: 11월이면 10)
        const startOfMonth = new Date(year, month, 1);
        const endOfMonth = new Date(year, month + 1, 0); // 다음 월의 0일은 이전 월의 마지막 날

        fetch(`/api/game/${team.teamId}`, {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({
                title: formData.title,
                content: formData.content,
                gameId: formData.gameId,  // gameId를 함께 전송
                location: formData.location,
            })})
            .then((res) => res.json())
            .then((data) => {
                if (Array.isArray(data)) {
                    setGameData(data); // 배열이면 그대로 상태에 저장
                } else {
                    setGameData([]); // 배열이 아니면 빈 배열로 설정
                }// 서버에서 받은 경기 데이터 저장
            })
            .catch((err) => console.error("Failed to fetch game data:", err));
    }, [selectedDate, team.teamId]);

    // 날짜가 선택되었을 때 해당 날짜의 경기 정보를 필터링하여 저장
    useEffect(() => {
        const selectedDateString = selectedDate.toISOString().split("T")[0]; // 선택된 날짜의 yyyy-mm-dd 형식
        const gamesOnSelectedDate = gameData.filter((game) => {
            return game.gameDate === selectedDateString; // 경기가 선택된 날짜와 일치하는지 확인
        });
        setGamesForSelectedDate(gamesOnSelectedDate);
    }, [selectedDate, gameData]);

    return (
        <div className={styles.default}>
            <div className={styles.cropping}
                 style={{backgroundColor: backgroundColor}}></div>
                <div className={styles.inner}>
                    <div className={styles.title}>{team.sponsor} 경기 일정</div>

                    <div className={styles.calendarContainer}>
                        <Calendar
                            onChange={setSelectedDate} // 날짜 선택 핸들러
                            value={selectedDate} // 현재 선택된 날짜
                        />
                    </div>

                    <div className={styles.gameList}>
                        <h3>{selectedDate.toLocaleDateString()} 경기를 확인하세요:</h3>
                        {gamesForSelectedDate.length > 0 ? (
                            <ul>
                                {gamesForSelectedDate.map((game) => (
                                    <li key={game.gameId}>
                                        {game.homeTeam.teamName} vs {game.awayTeam.teamName} - {game.gameDate}
                                    </li>
                                ))}
                            </ul>
                        ) : (
                            <p>이번 날짜에는 경기가 없습니다.</p>
                        )}
                    </div>
                </div>
                <Footer/>
            </div>
            );
            }
