import styles from "./PlayerDetail.module.css";
import React, {useEffect, useState} from "react";
import Footer from "../../components/_Layout/Footer";
import { useParams} from 'react-router-dom';
import SingleTable from "../../components/_Table/SingleTable";
import {getPosition} from '../../components/_Table/PlayerTable'

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faHeart as faHeartFull} from "@fortawesome/free-solid-svg-icons";
import { faHeart as faHeartEmpty} from "@fortawesome/free-regular-svg-icons";
import PlayerScore from "../../components/_List/PlayerScore";

export default function PlayerDetail() {
    const [player, setPlayer] = useState(null);
    const { pId } = useParams();
    const [isFavorite, setIsFavorite] = useState(false);

    useEffect(() => {
        fetch(`/api/player/${pId}`)
            .then(res=>res.json())
            .then((data) => {
                setPlayer(data);
            })
            .catch((err) => {
                console.error("API fetch error:", err);
            });
    }, [pId]);

    return (
        <div className={styles.default}>
            <div className={styles.cropping}>
            </div>
            <div className={styles.inner}>
                {player ? (
                    <div>
                        <FontAwesomeIcon className={styles.ii} icon={faHeartEmpty}/>
                        <FontAwesomeIcon className={styles.ii} icon={faHeartFull}/>
                        {player.playerPosition === 1 ? (
                            <p className={styles.emoji}>üß¢</p>
                        ) : <p className={styles.emoji}>‚öæÔ∏è</p>}
                        <p className={styles.title}>{player.playerName}</p>

                        <SingleTable
                            iconName={"faBaseball"}
                            type={"Îì±Î≤àÌò∏"}
                            data={player.playerNumber}
                        />
                        <SingleTable
                            iconName={"faTicket"}
                            type={"Ìè¨ÏßÄÏÖò"}
                            data={getPosition(player.playerPosition)}
                        />
                        {player ?
                            (
                                <PlayerScore
                                    position={player.playerPosition}
                                    scoreData={player.scoreData}
                                />
                            ) : <p>Loading...</p>
                        }

                    </div>
                ) : (
                    <p>Loading...</p>  // Î°úÎî© Ï§ë
                )}

            </div>
            <Footer/>
        </div>
    );
}
