import React from "react";
import styles from "./Table.module.css";

export default function Table({ title, date, homeTeam, awayTeam }) {
    // Assuming you're passing an array of data, each with title, date, homeTeam, awayTeam
    const data = [
        { title: 'Game 1', date: '2024-11-20', homeTeam: 'Team A', awayTeam: 'Team B' },
        { title: 'Game 2', date: '2024-11-21', homeTeam: 'Team C', awayTeam: 'Team D' },
        // More data can be added here
    ];

    return (
        <div className={styles.tableWrapper}>
            <table className={styles.table}>
                <thead>
                <tr>
                    <th className={styles.th}>제목</th>
                    <th className={styles.th}>날짜</th>
                    <th className={styles.th}>홈팀</th>
                    <th className={styles.th}>상대팀</th>
                </tr>
                </thead>
                <tbody>
                {data.length > 0 ? (
                    data.map((row, rowIndex) => (
                        <tr key={rowIndex} className={styles.tr}>
                            <td className={styles.td}>{row.title}</td>
                            <td className={styles.td}>{row.date}</td>
                            <td className={styles.td}>{row.homeTeam}</td>
                            <td className={styles.td}>{row.awayTeam}</td>
                        </tr>
                    ))
                ) : (
                    <tr>
                        <td colSpan={4} className={styles.noData}>데이터가 없습니다.</td>
                    </tr>
                )}
                </tbody>
            </table>
        </div>
    );
}
