import styles from "./Mainpage.module.css";
import React, {useEffect, useState} from "react";
import Footer from "../../components/_Layout/Footer";
import HeaderImg from "../../image/nail.png";
import Table from "../../components/_Tool/Table"

export default function Mainpage() {
    const [number, setData] = useState([]);



    useEffect(() => {
        fetch("/api/watching")
            .then((response) => {
                console.log(response); // 서버 응답 확인
                return response.json(); // JSON 데이터를 반환
            })
            .then((data) => {
                console.log(data); // 받아온 데이터 출력
                setData(data); // 데이터를 state에 설정
            })
            .catch((err) => {
                console.error("API fetch error:", err); // 에러 처리
            });
    }, []);

    return (
        <div className={styles.default}>
            <div className={styles.cropping}>
                <img className={styles.headerImg} src={HeaderImg} alt="Header" />
            </div>
            <div className={styles.inner}>
                <div className={styles.title}>2024 KBO</div>
                다가오는 경기
                {number.map(data => <Rank  rankNo={data.ranking} rank={data.rankName} id={data.githubName}
                                           score={data.score} tier={data.bojRank}/>)}
            </div>
            <Footer />
        </div>
    );
}
