import styles from "./Log.module.css";
import React, {useEffect, useState} from "react";
import Footer from "../../components/_Layout/Footer";
import { useParams } from 'react-router-dom';
import SingleTable from "../../components/_Tool/SingleTable";
import Table from "../../components/_Tool/Table";

export default function Log() {
    const [watchingContent, setContent] = useState(null);
    const { id } = useParams();

    useEffect(() => {
        fetch(`/api/log/${id}`)
            .then(res=>res.json())
            .then((data) => {
                setContent(data);  // 관람 기록 데이터를 상태에 저장
            })
            .catch((err) => {
                console.error("API fetch error:", err); // 에러 처리
            });
    }, []);
    console.log(watchingContent)

    return (
        <div className={styles.default}>
            <div className={styles.cropping}>
            </div>
            <div className={styles.inner}>
                {watchingContent ? (
                    <div>
                        <p className={styles.emoji}>{watchingContent.title.slice(0, 3)}</p>
                        <p className={styles.title}>{watchingContent.title.slice(3)}</p>
                        경기
                        <SingleTable
                            date={(() => {
                                const gameDate = new Date(watchingContent.game.gameDate);
                                const year = gameDate.getFullYear();
                                const month = String(gameDate.getMonth() + 1).padStart(2, '0');  // getMonth() returns 0-11
                                const day = String(gameDate.getDate()).padStart(2, '0');
                                return `${year}년 ${month}월 ${day}일`;
                            })()}
                            location={watchingContent.location}
                            homeTeam={watchingContent.game.homeTeam.teamName}
                            awayTeam={watchingContent.game.awayTeam.teamName}
                        />
                        <p>Location: {watchingContent.location}</p>vs
                        <p>Game: {watchingContent.game.homeTeam.teamName} vs {watchingContent.game.awayTeam.teamName}</p>
                        <p>{watchingContent.content}</p>

                    </div>
                ) : (
                    <p>Loading...</p>  // 데이터가 로딩 중일 때
                )}

            </div>
            <Footer/>
        </div>
    );
}
