import styles from "./Mainpage.module.css";
import React, {useEffect, useState} from "react";
import Footer from "../../components/_Layout/Footer";
import HeaderImg from "../../image/nail.png";
import Table from "../../components/_Tool/Table"

export default function Mainpage() {
    const [record, setRecord] = useState([]);
    let token = localStorage.getItem('username') || '';



    useEffect(() => {
        // 로그인된 사용자의 정보를 확인 (localStorage 또는 sessionStorage에서 가져옴)
        let username = localStorage.getItem('username') || sessionStorage.getItem('name') || '';

        // 사용자가 로그인한 경우에만 API 호출
        if (username) {
            fetch("/api/watching", {
                method: "GET",
                headers: {
                    "Content-Type": "application/json",
                },
                credentials: "include",  // 쿠키 및 세션 관리
            })
                .then((res) => res.json())
                .then((res) => {
                    if (res) {
                        setRecord(res);  // watching 기록 업데이트
                    }
                })
                .catch((err) => {
                    setError("API fetch error: " + err.message);  // 에러 처리
                });
        } else {
            setError("로그인되지 않은 사용자입니다.");
        }
    }, []);

    return (
        <div className={styles.default}>
            <div className={styles.cropping}>
                <img className={styles.headerImg} src={HeaderImg} alt="Header" />
            </div>
            <div className={styles.inner}>
                <div className={styles.title}>2024 KBO</div>
                <br/>다가오는 경기 - 추후 기능 추가<br/><br/>
                {record.map(data => (
                    <Table
                        id={data.watchingId}
                        title={data.title}
                        date={(() => {
                            const gameDate = new Date(data.game.gameDate);
                            const year = gameDate.getFullYear();
                            const month = String(gameDate.getMonth() + 1).padStart(2, '0');  // getMonth() returns 0-11
                            const day = String(gameDate.getDate()).padStart(2, '0');
                            return `${year}년 ${month}월 ${day}일`;
                        })()}
                        location={data.location}
                        homeTeam={data.game.homeTeam.teamName}
                        awayTeam={data.game.awayTeam.teamName}
                    />
                ))}


            </div>
            <Footer />
        </div>
    );
}
