import { useNavigate } from "react-router-dom";
import React, {useState} from "react";
import styles from "./Signin.module.css"

export default function Signup() {
    const navigate = useNavigate();

    const [userid, setUserid] = useState('');
    const [userpassword, setUserpw] = useState('');
    // 버튼 상태

    const userData = {
        user_id : userid,
        user_password : userpassword
    }
    
    const saveId = event =>{
        setUserid(event.target.value);
        console.log(event.target.value);
    }

    const savePw = event =>{
        setUserpw(event.target.value);
        console.log(event.target.value);
    }

    const checkDup = async (e) => {
        e.preventDefault();
        console.log(userid);
        if(userid<3){
            alert('깃허브 ID를 제대로 입력해주세요')
            return;
        }
        try {
            const response = await fetch("http://localhost:8080/valid/githubid", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(userid),
            });

            console.log("Response status:", response.status);

            if (response.ok) {
                setUserid(true);
                alert("중복확인 완료");
            } else if (response.status === 400) {
                const errorText = await response.text();
                alert(`중복된 ID입니다.`);
            } else {
                console.error("중복확인에 실패하였습니다.", response.statusText);
            }
        } catch (error) {
            console.error("중복확인 실패3 : ", error);
        }//엄!
    };


    const sign = async (e) => {
        e.preventDefault();
    
        console.log(userData);
    // 코드 확인 과정 추가
        try {
            const response = await fetch("http://localhost:8080/member/signup", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(userData),
            });
    
            console.log("Response status:", response.status);
            
            if (response.ok) {
                alert("회원가입에 성공하셨습니다.");
                window.location.replace("/");
            } else if (response.status === 400) {
                const errorText = await response.text();
                alert(`일치하지 않습니다. ${errorText}`);
                console.error("회원가입 실패 : ", errorText);
            } else {
                console.error("회원가입 실패 : ", response.statusText);
            }
        } catch (error) {
            console.error("회원가입 실패 : ", error);
        }
    };
    
    

    return (
        <div className={styles.signupPage}>
            <div>
            <div className={styles.title}>크보로그</div>
                <div className={styles.inputArea}>
                <input className={styles.inputId} value={userid} onChange={saveId} Type="text"
                    placeholder="ID를 입력해주세요." />
                <button onClick={checkDup}  className="text-wrapper-7, div-wrapper">중복확인</button>
                </div>
                <div className={styles.inputArea}>

                <input value={userpassword} onChange={savePw} Type="password"
                    placeholder="비밀번호를 입력해주세요." />
                    </div>
                <button onClick={sign} className={styles.signin}>회원가입</button>

            </div>
        </div>
    );

};
