import styles from "./PlayerDetail.module.css";
import React, {useEffect, useState} from "react";
import Footer from "../../components/_Layout/Footer";
import {useNavigate, useParams} from 'react-router-dom';
import SingleTable from "../../components/_Table/SingleTable";
import {getPosition, playerPosition} from '../../components/_Table/PlayerTable'

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPenToSquare,faCircleXmark} from "@fortawesome/free-regular-svg-icons";

export default function PlayerDetail() {
    const [player, setPlayer] = useState(null);
    const { pId } = useParams();

    useEffect(() => {
        fetch(`/api/player/${pId}`)
            .then(res=>res.json())
            .then((data) => {
                setPlayer(data);
                console.log(data)
            })
            .catch((err) => {
                console.error("API fetch error:", err);
            });
    }, []);

    return (
        <div className={styles.default}>
            <div className={styles.cropping}>
            </div>
            <div className={styles.inner}>
                {player ? (
                    <div>
                        <p className={styles.emoji}>🧢</p>
                        <p className={styles.title}>{player.playerName}</p>

                        <SingleTable
                            iconName={"faBaseball"}
                            type={"등번호"}
                            data={player.playerNumber}
                        />
                        <SingleTable
                            iconName={"faTicket"}
                            type={"포지션"}
                            data={getPosition(player.playerPosition)}
                        />
                        {/*<p className={styles.content}>{player.content}</p>*/}
                    </div>
                ) : (
                    <p>Loading...</p>  // 로딩 중
                )}

            </div>
            <Footer/>
        </div>
    );
}
