import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Test() {
    const [stadium_id, setId] = useState('');
    const [price, setName] = useState('');

    //db 데이터
    const [entities, setEntities] = useState([]);

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = async () => {
        try {
            const response = await axios.get('/api/stadium');
            setEntities(response.data);
        } catch (error) {
            console.error('데이터 불러오기 에러:', error);
        }
    };

    const onSubmit = async (e) => {
        e.preventDefault();
        console.log('전송할 데이터:', { stadium_id, price });
        try {
            const response = await axios.post('http://localhost:8080/api/stadium', {
                stadium_id: stadium_id,
                price: price,
            });
            console.log('데이터 전달 성공: ', response.data);
            fetchData();
        } catch (error) {
            console.error('데이터 전달 에러:', error);
        }
    };

    return (
        <div>
            <form onSubmit={onSubmit}>
                아이디: <input type="text" value={stadium_id} onChange={(e) => setId(e.target.value)}></input>
                가격:<input type="text" value={price} onChange={(e) => setName(e.target.value)}></input>
                <button type="submit">전송</button>
            </form>

            <div>
                <h2>TestEntity DB</h2>
                <ul>
                    {entities.map((entity) => (
                        <li key={entity.stadium_id}>
                            아이디: {entity.stadium_id} / 가격: {entity.stadium_name}
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
}

export default Test;
