import styles from "./Mypage.module.css";
import React, {useEffect, useState} from "react";
import Footer from "../../components/_Layout/Footer";
import SingleTable from "../../components/_Tool/SingleTable";

import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faMarker} from "@fortawesome/free-solid-svg-icons";
import {useNavigate} from "react-router-dom";

export default function Mypage() {
    const [user, setUser] = useState([]);
    const [rootTeam, setRootTeam] = useState([]);
    const [teams, setTeams] = useState([]);
    const [selectedTeamId, setSelectedTeamId] = useState("");
    const navigate = useNavigate();
    const handleLogEdit = () => {
        navigate(`/my/edit`);
    };
    useEffect(() => {
        fetch("/api/my")
            .then(res => res.json())
            .then(res => {
                setUser(res);
                setRootTeam(res.rootTeam.teamName)
            })
            .catch((err) => {
                console.error("API fetch error:", err); // ÏóêÎü¨ Ï≤òÎ¶¨
            });
    }, []);

    // ÏùëÏõê ÌåÄ ÏÑ†ÌÉù
    useEffect(() => {
        fetch("/api/my/team") // ÌåÄ Î™©Î°ùÏùÑ Í∞ÄÏ†∏Ïò§Îäî API ÏóîÎìúÌè¨Ïù∏Ìä∏
            .then((res) => res.json())
            .then((data) => {
                setTeams(data); // Í∞ÄÏ†∏Ïò® Îç∞Ïù¥ÌÑ∞Î•º ÏÉÅÌÉúÏóê Ï†ÄÏû•
            })
            .catch((err) => {
                console.error("Failed to fetch teams:", err);
            });
    }, []);

    // ÌåÄ ÏÑ†ÌÉù Ìï∏Îì§Îü¨
    const handleTeamSelect = (e) => {
        const teamId = e.target.value;
        setSelectedTeamId(teamId); // ÏÑ†ÌÉùÌïú ÌåÄ ID Ï†ÄÏû•
        console.log("Selected team ID:", teamId);
    };

    // Ìèº Ï†úÏ∂ú Ìï∏Îì§Îü¨
    const handleSubmit = (e) => {
        e.preventDefault();

        // ÏÑ†ÌÉùÌïú ÌåÄ Îç∞Ïù¥ÌÑ∞ ÏÑúÎ≤ÑÎ°ú Ï†ÑÏÜ°
        fetch(`/api/my/selectTeam`, {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({
                teamId: selectedTeamId,
            }),
        })
            .then((res) => {
                if (res.ok) {
                    alert("ÌåÄ ÏÑ†ÌÉù ÏôÑÎ£å!");
                } else {
                    throw new Error("Failed to save selected team");
                }
            })
            .catch((err) => {
                console.error("Failed to save selected team:", err);
            });
    };


    return (
        <div className={styles.default}>
            <div className={styles.cropping}>
            </div>
            <div className={styles.inner}>
                <div>
                    <FontAwesomeIcon className={styles.ii} icon={faMarker} onClick={handleLogEdit}/>
                    <p className={styles.emoji}>üíü</p>
                    <p className={styles.title}>ÎßàÏù¥ÌéòÏù¥ÏßÄ</p>
                    <SingleTable
                        iconName={"faUser"}
                        type={"ÏïÑÏù¥Îîî"}
                        data={user.username}
                    />
                    <SingleTable
                        iconName={"faBaseball"}
                        type={"ÏùëÏõê ÌåÄ"}
                        data={rootTeam}
                    />
                    <SingleTable
                        iconName={"faCalendarCheck"}
                        type={"ÏùëÏõê ÎÇ†Ïßú"}
                        data={user.rootdate}
                    />
                    <p className={styles.content}></p>
                </div>
            </div>
            <Footer/>
        </div>
    );
}
